arguments.argument:
if(place_meeting(x+sign(hspeed), y, wallchunk) and place_meeting(x+sign(hspeed), y-1, wallchunk))
{
    hspeed = 0;
    oldspace = iterspace;
    iterspace = point_distance(oldx, 0, x, olddy);
    x = round(x);
    if(debug)
        debugstring += "NTT:" + string(iterspace) + "; ";
}
if(place_meeting(x, y+sign(vspeed), wallchunk))
{
    vspeed = 0;
    oldspace = iterspace;
    iterspace = point_distance(0, oldy, olddx, y);
    y = round(y);
    if(debug)
        debugstring += "NTT:" + string(iterspace) + "; ";
}
if(place_meeting(x+sign(hspeed), y+sign(vspeed), wallchunk) and place_meeting(x+sign(hspeed), y-1, wallchunk))
{
    if(!place_meeting(x+sign(hspeed), y, wallchunk))
    {
        vspeed = 0;
        oldspace = iterspace;
        iterspace = point_distance(0, oldy, olddx, y);
        y = round(y);
        if(debug)
            debugstring += "NTT:" + string(iterspace) + "; ";
    }
    else
    {
        hspeed = 0;
        oldspace = iterspace;
        iterspace = point_distance(oldx, 0, x, olddy);
        x = round(x);
        if(debug)
            debugstring += "NTT:" + string(iterspace) + "; ";
    }
}
